SELECT ok(
    EXISTS(
        SELECT true
          FROM pg_catalog.pg_namespace
         WHERE nspname = $1
    ), $2
);

SELECT ok(
    _rexists( 'S', $1, $2 ),
    'Sequence ' || quote_ident($1) || '.' || quote_ident($2) || ' should exist'
);

SELECT ok(
    _rexists( '{r,p}'::char[], $1, $2 ),
    'Table ' || quote_ident($1) || '.' || quote_ident($2) || ' should exist'
);

SELECT ok(
    EXISTS(
        SELECT true
          FROM pg_catalog.pg_tablespace
         WHERE spcname = $1
    ), $2
);

BEGIN
    IF pg_version_num() >= 90200 THEN
        RETURN ok(
            EXISTS(
                SELECT true
                  FROM pg_catalog.pg_tablespace
                 WHERE spcname = $1
                   AND pg_tablespace_location(oid) = $2
            ), $3
        );
    ELSE
        RETURN ok(
            EXISTS(
                SELECT true
                  FROM pg_catalog.pg_tablespace
                 WHERE spcname = $1
                   AND spclocation = $2
            ), $3
        );
    END IF;
END;

