This function keeps track of modifications made to an audit
table. It stores the previous values and new values. For first insert it
stores all values.


DECLARE
    v_id UUID;
    v_table TEXT;
    v_hnew hstore;
    v_hold hstore;
    v_key  TEXT;
    v_skip BOOLEAN;
BEGIN

    v_id := NULL;
    v_table := regexp_replace ( TG_TABLE_NAME, '_[0-9]{6}$', '' );

    -- For partitioned tables, we will record only the base name of the table

    CASE TG_OP
    WHEN 'INSERT' THEN
        v_hnew := hstore(NEW);

        FOREACH v_key IN ARRAY akeys(v_hnew) LOOP
            IF v_key = 'id' THEN
               v_id := v_hnew->v_key;
               EXIT;
            END IF;
        END LOOP;

        INSERT INTO audit_trail_log ( created_by, table_name, operation, 
                                  object_id, new_value,
                                  statement_date )
        VALUES ( current_user, v_table, TG_OP, v_id, v_hnew, 
                 clock_timestamp() );

        RETURN NEW;

    WHEN 'DELETE' THEN
        v_hold := hstore(OLD);

        FOREACH v_key IN ARRAY akeys(v_hold) LOOP
            IF v_key = 'id' THEN
               v_id := v_hold->v_key;
               EXIT;
            END IF;
        END LOOP;

        INSERT INTO audit_trail_log ( created_by, table_name, operation, 
                                  object_id, old_value,
                                  statement_date )
        VALUES ( current_user, v_table, TG_OP, v_id, v_hold,
                 clock_timestamp() );

        RETURN OLD;

    WHEN 'UPDATE' THEN
        v_hold := hstore(OLD);
        v_hnew := hstore(NEW);

        FOREACH v_key IN ARRAY akeys(v_hnew) LOOP
            IF v_key = 'id' THEN
               v_id := v_hnew->v_key;
               EXIT;
            END IF;
        END LOOP;

        v_hnew = v_hnew - v_hold; -- Do not add items that did not change.

        -- Do not record updates that only touch updated_* columns to
        -- save space.

        v_skip := true;

        FOREACH v_key IN ARRAY akeys(v_hnew) LOOP
            IF v_key !~* '^updated_' THEN
               v_skip := false;
               EXIT;
            END IF;
        END LOOP;

        IF akeys(v_hnew) <> array[]::text[] AND NOT v_skip THEN

           INSERT INTO audit_trail_log ( created_by, table_name, operation, 
                                     object_id, old_value, new_value,
                                     statement_date )
           VALUES ( current_user, v_table, TG_OP, v_id, 
                    v_hold, v_hnew, clock_timestamp() );
        END IF;

        RETURN NEW;
    END CASE;
END;

