SELECT performs_ok(
    $1, $2, 'Should run in less than ' || $2 || ' ms'
);

DECLARE
    query     TEXT := _query($1);
    max_time  ALIAS FOR $2;
    descr     ALIAS FOR $3;
    starts_at TEXT;
    act_time  NUMERIC;
BEGIN
    starts_at := timeofday();
    EXECUTE query;
    act_time := extract( millisecond from timeofday()::timestamptz - starts_at::timestamptz);
    IF act_time < max_time THEN RETURN ok(TRUE, descr); END IF;
    RETURN ok( FALSE, descr ) || E'\n' || diag(
           '      runtime: ' || act_time || ' ms' ||
        E'\n      exceeds: ' || max_time || ' ms'
    );
END;

SELECT performs_within(
          $1, $2, $3, 10,
          'Should run within ' || $2 || ' +/- ' || $3 || ' ms');

SELECT performs_within(
          $1, $2, $3, $4,
          'Should run within ' || $2 || ' +/- ' || $3 || ' ms');

DECLARE
    query          TEXT := _query($1);
    expected_avg   ALIAS FOR $2;
    within         ALIAS FOR $3;
    iterations     ALIAS FOR $4;
    descr          ALIAS FOR $5;
    avg_time       NUMERIC;
BEGIN
  SELECT avg(a_time) FROM _time_trials(query, iterations, 0.8) t1 INTO avg_time;
  IF abs(avg_time - expected_avg) < within THEN RETURN ok(TRUE, descr); END IF;
  RETURN ok(FALSE, descr) || E'\n' || diag(' average runtime: ' || avg_time || ' ms'
     || E'\n desired average: ' || expected_avg || ' +/- ' || within || ' ms'
    );
END;

SELECT performs_within(
          $1, $2, $3, 10, $4
        );

DECLARE
    rcount INTEGER;
BEGIN
    BEGIN
        EXECUTE '
            CREATE TEMP SEQUENCE __tcache___id_seq;
            CREATE TEMP TABLE __tcache__ (
                id    INTEGER NOT NULL DEFAULT nextval(''__tcache___id_seq''),
                label TEXT    NOT NULL,
                value INTEGER NOT NULL,
                note  TEXT    NOT NULL DEFAULT ''''
            );
            CREATE UNIQUE INDEX __tcache___key ON __tcache__(id);
            GRANT ALL ON TABLE __tcache__ TO PUBLIC;
            GRANT ALL ON TABLE __tcache___id_seq TO PUBLIC;

            CREATE TEMP SEQUENCE __tresults___numb_seq;
            GRANT ALL ON TABLE __tresults___numb_seq TO PUBLIC;
        ';

    EXCEPTION WHEN duplicate_table THEN
        -- Raise an exception if there's already a plan.
        EXECUTE 'SELECT TRUE FROM __tcache__ WHERE label = ''plan''';
      GET DIAGNOSTICS rcount = ROW_COUNT;
        IF rcount > 0 THEN
           RAISE EXCEPTION 'You tried to plan twice!';
        END IF;
    END;

    -- Save the plan and return.
    PERFORM _set('plan', $1 );
    PERFORM _set('failed', 0 );
    RETURN '1..' || $1;
END;

DECLARE
  v_contact_id     UUID;
  v_create_domain   RECORD;
BEGIN

  SELECT id INTO v_contact_id FROM order_contact WHERE id = NEW.reference_id;

  IF NOT FOUND THEN 
    RAISE EXCEPTION 'reference id % not found in order_contact table',NEW.reference_id;
  END IF;

  -- order information
  SELECT * INTO v_create_domain 
    FROM v_order_create_domain 
  WHERE order_item_id = NEW.order_item_id; 

  -- thanks to the magic from inheritance, the contact table already
  -- contains the data, we just need to materialize it there.
  INSERT INTO contact (SELECT * FROM contact WHERE id=NEW.reference_id);
  INSERT INTO contact_postal (SELECT * FROM contact_postal WHERE contact_id=NEW.reference_id);

  -- we now signal the provisioning 
  INSERT INTO provision_contact(
    contact_id,
    accreditation_id,
    tenant_customer_id,
    order_item_plan_ids
  ) VALUES(
    NEW.reference_id,
    v_create_domain.accreditation_id,
    v_create_domain.tenant_customer_id,
    ARRAY[NEW.id]
  ) ON CONFLICT (contact_id,accreditation_id) 
      DO UPDATE 
        SET order_item_plan_ids = provision_contact.order_item_plan_ids || EXCLUDED.order_item_plan_ids;

  RETURN NEW;
END;

DECLARE v_domain RECORD; v_create_domain RECORD; v_pd_id UUID; BEGIN
-- order information SELECT * INTO v_create_domain FROM v_order_create_domain WHERE order_item_id = NEW.order_item_id;
-- we now signal the provisioning WITH pd_ins AS ( INSERT INTO provision_domain( name, registration_period, accreditation_id, accreditation_tld_id, tenant_customer_id, order_item_plan_ids ) VALUES( v_create_domain.domain_name, v_create_domain.registration_period, v_create_domain.accreditation_id, v_create_domain.accreditation_tld_id, v_create_domain.tenant_customer_id, ARRAY[NEW.id] ) RETURNING id ) SELECT id INTO v_pd_id FROM pd_ins;
-- insert contacts INSERT INTO provision_domain_contact( provision_domain_id, contact_id, contact_type_id ) ( SELECT v_pd_id, order_contact_id, domain_contact_type_id FROM create_domain_contact WHERE create_domain_id = NEW.order_item_id );
-- insert hosts INSERT INTO provision_domain_host( provision_domain_id, host_id ) ( SELECT v_pd_id, h.id FROM host h WHERE h.name IN ( SELECT name FROM create_domain_nameserver WHERE create_domain_id = NEW.order_item_id ) AND tenant_customer_id = v_create_domain.tenant_customer_id );
UPDATE provision_domain SET is_complete = TRUE WHERE id = v_pd_id;
RETURN NEW; END;

DECLARE v_create_domain RECORD; v_dc_host RECORD; v_new_host_id UUID; v_p_host RECORD; BEGIN
SELECT * INTO v_dc_host FROM create_domain_nameserver n WHERE n.id = NEW.reference_id;
IF NOT FOUND THEN RAISE EXCEPTION 'reference id % not found in create_domain_nameserver table', NEW.reference_id; END IF;
-- load the order information through the v_order_create_domain view SELECT * INTO v_create_domain FROM v_order_create_domain WHERE order_item_id = NEW.order_item_id;
-- check to see if the host is already provisioned in the -- instance
SELECT ps.name AS status_name, ps.is_final AS status_is_final, ps.is_success AS status_is_success INTO v_p_host FROM provision_host ph JOIN host h ON h.id = ph.host_id JOIN provision_status ps ON ps.id = ph.status_id WHERE accreditation_id = v_create_domain.accreditation_id AND h.name = v_dc_host.name AND ps.name='completed';
IF NOT FOUND THEN
-- upsert the host WITH new_host AS ( INSERT INTO host(tenant_customer_id,name) VALUES(v_create_domain.tenant_customer_id,v_dc_host.name) ON CONFLICT (tenant_customer_id,name) DO UPDATE SET updated_date=NOW() RETURNING id ) SELECT id INTO v_new_host_id FROM new_host;
-- insert the addresses INSERT INTO host_addr(host_id,address) ( SELECT v_new_host_id, ha.addr FROM create_domain_nameserver_addr ha JOIN create_domain_nameserver h ON h.id = ha.nameserver_id WHERE h.create_domain_id = NEW.order_item_id AND h.id = NEW.reference_id ) ON CONFLICT DO NOTHING;
-- send the host to be provisioned -- but if there's a record that's pending -- simply add ourselves to those order_item_plan_ids that need -- to be updated INSERT INTO provision_host( accreditation_id, host_id, tenant_customer_id, order_item_plan_ids ) VALUES ( v_create_domain.accreditation_id, v_new_host_id, v_create_domain.tenant_customer_id, ARRAY[NEW.id] ) ON CONFLICT (host_id,accreditation_id) DO UPDATE SET order_item_plan_ids = provision_host.order_item_plan_ids || EXCLUDED.order_item_plan_ids;
ELSE -- host has already been provisioned, we can mark this as complete UPDATE create_domain_plan SET status_id = tc_id_from_name('order_item_plan_status','completed') WHERE id = NEW.id;
END IF;
RETURN NEW; END;

DECLARE
  v_object            RECORD; 
  v_order             RECORD;
  v_accreditation_tld RECORD;
  v_previous_id       UUID;
  v_previous_rank     INT DEFAULT 1;
  v_previous_parent   UUID;
  v_parent            UUID;
  v_related_obj       RECORD;
BEGIN

  -- load the order
  SELECT * INTO v_order FROM "order" WHERE id=NEW.order_id;

  -- load accreditation
  SELECT * INTO v_accreditation_tld FROM v_accreditation_tld WHERE accreditation_tld_id = NEW.accreditation_tld_id;

  -- loop through the strategy to create a plan
  FOR v_object IN SELECT
                      RANK() OVER (ORDER BY provision_order DESC) AS rank,
                      object_name,
                      object_id,
                      provision_order,
                      product_name,
                      order_type_name
                  FROM v_provider_instance_order_item_strategy 
                  WHERE 
                    provider_instance_id=v_accreditation_tld.provider_instance_id
                    AND order_type_id = v_order.type_id
                  ORDER BY 1
  LOOP 

    IF v_previous_id IS NOT NULL THEN 
      IF v_object.rank = v_previous_rank THEN 
        v_parent := v_previous_parent;
      ELSE 
        v_parent := v_previous_id;
      END IF;
    END IF;

    FOR v_related_obj IN SELECT * FROM v_order_item_plan_object 
                            WHERE order_item_id = NEW.id 
                                  AND object_id=v_object.object_id
    LOOP

      EXECUTE FORMAT('INSERT INTO %s_%s_plan(
          order_item_id,
          parent_id,
          order_item_object_id,
          reference_id
        )
        VALUES ($1,$2,$3,$4) 
        RETURNING id',v_object.order_type_name,v_object.product_name)
        INTO v_previous_id
        USING 
          NEW.id, 
          v_parent, 
          v_object.object_id,
          v_related_obj.id;

    END LOOP;

    v_previous_rank := v_object.rank;
    v_previous_parent := v_parent;

  END LOOP;

  RETURN NEW;

END;

DECLARE
  v_domain          RECORD;
  v_renew_domain   RECORD;
  v_pd_id           UUID;
BEGIN

    -- order information
  SELECT * INTO v_renew_domain 
    FROM v_order_renew_domain 
  WHERE order_item_id = NEW.order_item_id; 

  -- -- we now signal the provisioning 
  INSERT INTO provision_domain_renew(
    domain_id,
    period,
    accreditation_id,
    tenant_customer_id,
    current_expiry_date,
    order_item_plan_ids
  ) VALUES(
    v_renew_domain.domain_id,
    v_renew_domain.period,
    v_renew_domain.accreditation_id,
    v_renew_domain.tenant_customer_id,
    v_renew_domain.current_expiry_date,
    ARRAY[NEW.id]
  );


  RETURN NEW;
END;

