    $1, $2, $3,
    'Policy ' || quote_ident($2)
    || ' for table ' || quote_ident($1)
    || ' should apply to ' || upper($3) || ' command'
);

DECLARE
  v_status      RECORD;
  v_oi_plan_id   UUID;
BEGIN

  SELECT * INTO v_status FROM provision_status WHERE id = NEW.status_id;

  IF NOT v_status.is_final THEN 
    RETURN NEW;
  END IF;

  IF NOT v_status.is_success THEN 
    EXECUTE FORMAT('DELETE FROM %s WHERE id=$1',TG_RELNAME) USING NEW.id;
  END IF;

  RETURN NEW;
END;

DECLARE v_contact RECORD; BEGIN
WITH contact_info AS ( SELECT JSONB_AGG(TO_JSONB(cp.*)) AS postals FROM ONLY contact_postal cp WHERE cp.contact_id = NEW.contact_id ) SELECT NEW.id AS provision_contact_id, NEW.tenant_customer_id AS tenant_customer_id, TO_JSONB(c.*) AS contact, contact_info.postals AS postals, TO_JSONB(a.*) AS accreditation, NEW.pw AS pw INTO v_contact FROM ONLY contact c JOIN v_accreditation a ON a.accreditation_id = NEW.accreditation_id JOIN contact_info ON TRUE WHERE c.id=NEW.contact_id;
UPDATE provision_contact SET job_id=job_create( NEW.tenant_customer_id, 'provision_contact_create', NEW.id, TO_JSONB(v_contact.*) ) WHERE id = NEW.id;
RETURN NEW; END;

DECLARE v_domain RECORD; hr RECORD; BEGIN
WITH contacts AS ( SELECT JSONB_AGG( JSONB_BUILD_OBJECT( 'type',ct.name, 'handle',pc.handle ) ) AS data FROM provision_domain pd JOIN provision_domain_contact pdc ON pdc.provision_domain_id=pd.id JOIN domain_contact_type ct ON ct.id=pdc.contact_type_id JOIN provision_contact pc ON pc.contact_id = pdc.contact_id JOIN provision_status ps ON ps.id = pc.status_id WHERE ps.is_success AND ps.is_final AND pd.id = NEW.id ), hosts AS ( SELECT ARRAY_AGG( h.name ) AS data FROM provision_domain pd JOIN provision_domain_host pdh ON pdh.provision_domain_id=pd.id JOIN host h ON h.id = pdh.host_id JOIN provision_host ph ON ph.host_id = h.id JOIN provision_status ps ON ps.id = ph.status_id WHERE ps.is_success AND ps.is_final AND pdh.provision_domain_id=NEW.id ) SELECT NEW.id AS provision_contact_id, tnc.id AS tenant_customer_id, d.name AS name, d.registration_period, d.pw AS pw, contacts.data AS contacts, hosts.data AS nameservers, TO_JSONB(a.*) AS accreditation, TO_JSONB(vat.*) AS accreditation_tld INTO v_domain FROM provision_domain d JOIN contacts ON TRUE JOIN hosts ON TRUE JOIN v_accreditation a ON a.accreditation_id = NEW.accreditation_id JOIN v_accreditation_tld vat ON vat.accreditation_tld_id = d.accreditation_tld_id JOIN tenant_customer tnc ON tnc.tenant_id = a.tenant_id WHERE d.id = NEW.id;
UPDATE provision_domain SET job_id = job_create( v_domain.tenant_customer_id, 'provision_domain_create', NEW.id, TO_JSONB(v_domain.*) ) WHERE id=NEW.id;
RETURN NEW; END;

DECLARE 
valid_date BOOL;
BEGIN 

  SELECT EXISTS( 
    SELECT TRUE FROM domain WHERE DATE(ry_expiry_date)=DATE(NEW.current_expiry_date)
    AND id = NEW.domain_id
  ) INTO valid_date;

  IF NOT valid_date THEN
    RAISE EXCEPTION 'request current_expiry_date does not match ry_expiry_date in domain table';
  END IF;

  RETURN NEW;

END;

DECLARE v_renew RECORD; BEGIN
SELECT NEW.id AS provision_domain_renew_id, tnc.id AS tenant_customer_id, TO_JSONB(a.*) AS accreditation, d.name AS domain_name, d.ry_expiry_date AS expiry_date, pr.period AS period INTO v_renew FROM provision_domain_renew pr JOIN v_accreditation a ON a.accreditation_id = NEW.accreditation_id JOIN tenant_customer tnc ON tnc.tenant_id = a.tenant_id JOIN domain d ON d.id=pr.domain_id WHERE pr.id = NEW.id;
UPDATE provision_domain_renew SET job_id=job_create( v_renew.tenant_customer_id, 'provision_domain_renew', NEW.id, TO_JSONB(v_renew.*) ) WHERE id = NEW.id;
RETURN NEW; END;

DECLARE
  v_add     INTERVAL;
BEGIN

  v_add := FORMAT('%s years',NEW.period)::INTERVAL;

  UPDATE domain 
    SET 
      expiry_date=expiry_date + v_add, 
      ry_expiry_date=COALESCE(NEW.ry_expiry_date,expiry_date + v_add)
    WHERE id = NEW.domain_id;

  RETURN NEW;
END;

BEGIN
-- domain INSERT INTO domain( id, tenant_customer_id, accreditation_tld_id, name, auth_info, roid, ry_created_date, ry_expiry_date, expiry_date ) ( SELECT pd.id, -- domain id pd.tenant_customer_id, pd.accreditation_tld_id, pd.name, pd.pw, pd.roid, COALESCE(pd.ry_created_date,pd.created_date), COALESCE(pd.ry_expiry_date,pd.created_date + FORMAT('%d years',pd.registration_period)::INTERVAL), COALESCE(pd.ry_expiry_date,pd.created_date + FORMAT('%d years',pd.registration_period)::INTERVAL) FROM provision_domain pd WHERE id = NEW.id );
-- contact association INSERT INTO domain_contact( domain_id, contact_id, domain_contact_type_id ) ( SELECT provision_domain_id, contact_id, contact_type_id FROM provision_domain_contact WHERE provision_domain_id = NEW.id );
-- host association INSERT INTO domain_host( domain_id, host_id ) ( SELECT provision_domain_id, host_id FROM provision_domain_host WHERE provision_domain_id = NEW.id );
RETURN NEW; END;

DECLARE
  v_status      RECORD;
  v_oi_plan_id   UUID;
BEGIN

  SELECT * INTO v_status FROM provision_status WHERE id = NEW.status_id;

  IF NOT v_status.is_final THEN 
    RETURN NEW;
  END IF;

  -- notify all the order_item_plan_ids that are pending 
  IF NEW.order_item_plan_ids IS NOT NULL THEN 

    FOR v_oi_plan_id IN SELECT UNNEST(NEW.order_item_plan_ids) AS id 
    LOOP 
      UPDATE order_item_plan 
      SET 
        status_id = (
          SELECT 
           id 
          FROM order_item_plan_status 
          WHERE is_success = v_status.is_success AND is_final
        ),
        result_data=(SELECT result_data FROM job WHERE id=NEW.job_id),
        result_message=(SELECT result_message FROM job WHERE id=NEW.job_id)
      WHERE id = v_oi_plan_id;
    END LOOP;

  END IF;

  IF v_status.is_success THEN 
    UPDATE class.provision SET provisioned_date=NOW() WHERE id = NEW.id;
  END IF;

  RETURN NEW;
END;

DECLARE v_host RECORD; BEGIN
WITH _host_info AS ( SELECT h.id AS host_id, name AS host_name, ARRAY_AGG(ha.address) FILTER (WHERE FAMILY(ha.address) = 4) AS ipv4_addr, ARRAY_AGG(ha.address) FILTER (WHERE FAMILY(ha.address) = 6) AS ipv6_addr FROM host h LEFT JOIN host_addr ha ON ha.host_id = h.id WHERE h.id = NEW.host_id GROUP BY 1,2 ) SELECT NEW.id AS provision_host_id, NEW.tenant_customer_id AS tenant_customer_id, TO_JSONB(hi.*) AS host, TO_JSONB(a.*) AS accreditation INTO v_host FROM _host_info hi JOIN v_accreditation a ON a.accreditation_id = NEW.accreditation_id;
UPDATE provision_host SET job_id=job_create( NEW.tenant_customer_id, 'provision_host_create', NEW.id, TO_JSONB(v_host.*) ) WHERE id = NEW.id;
RETURN NEW; END;

SELECT  ARRAY_AGG(a.value) FROM (
  SELECT (FLOOR(RANDOM() * (200-10 ) + 10))::INT AS value FROM generate_series(1,3)
) a

SELECT relation_owner_is(
    $1, $2,
    'Relation ' || quote_ident($1) || ' should be owned by ' || quote_ident($2)
);

SELECT relation_owner_is(
    $1, $2, $3,
    'Relation ' || quote_ident($1) || '.' || quote_ident($2) || ' should be owned by ' || quote_ident($3)
);

DECLARE
    owner NAME := _get_rel_owner($1, $2);
BEGIN
    -- Make sure the relation exists.
    IF owner IS NULL THEN
        RETURN ok(FALSE, $4) || E'\n' || diag(
            E'    Relation ' || quote_ident($1) || '.' || quote_ident($2) || ' does not exist'
        );
    END IF;

    RETURN is(owner, $3, $4);
END;

DECLARE
    owner NAME := _get_rel_owner($1);
BEGIN
    -- Make sure the relation exists.
    IF owner IS NULL THEN
        RETURN ok(FALSE, $3) || E'\n' || diag(
            E'    Relation ' || quote_ident($1) || ' does not exist'
        );
    END IF;

    RETURN is(owner, $2, $3);
END;

DECLARE
    want REFCURSOR;
    res  TEXT;
BEGIN
    OPEN want FOR SELECT $2[i]
    FROM generate_series(array_lower($2, 1), array_upper($2, 1)) s(i);
    res := results_eq($1, want, $3);
    CLOSE want;
    RETURN res;
END;

DECLARE
    have       ALIAS FOR $1;
    want       ALIAS FOR $2;
    have_rec   RECORD;
    want_rec   RECORD;
    have_found BOOLEAN;
    want_found BOOLEAN;
    rownum     INTEGER := 1;
    err_msg    text := 'details not available in pg <= 9.1';
BEGIN
    FETCH have INTO have_rec;
    have_found := FOUND;
    FETCH want INTO want_rec;
    want_found := FOUND;
    WHILE have_found OR want_found LOOP
        IF have_rec IS DISTINCT FROM want_rec OR have_found <> want_found THEN
            RETURN ok( false, $3 ) || E'\n' || diag(
                '    Results differ beginning at row ' || rownum || E':\n' ||
                '        have: ' || CASE WHEN have_found THEN have_rec::text ELSE 'NULL' END || E'\n' ||
                '        want: ' || CASE WHEN want_found THEN want_rec::text ELSE 'NULL' END
            );
        END IF;
        rownum = rownum + 1;
        FETCH have INTO have_rec;
        have_found := FOUND;
        FETCH want INTO want_rec;
        want_found := FOUND;
    END LOOP;

    RETURN ok( true, $3 );
EXCEPTION
    WHEN datatype_mismatch THEN
        GET STACKED DIAGNOSTICS err_msg = MESSAGE_TEXT;
        RETURN ok( false, $3 ) || E'\n' || diag(
            E'    Number of columns or their types differ between the queries' ||
            CASE WHEN have_rec::TEXT = want_rec::text THEN '' ELSE E':\n' ||
                '        have: ' || CASE WHEN have_found THEN have_rec::text ELSE 'NULL' END || E'\n' ||
                '        want: ' || CASE WHEN want_found THEN want_rec::text ELSE 'NULL' END
            END || E'\n        ERROR: ' || err_msg
        );
END;

DECLARE
    want REFCURSOR;
    res  TEXT;
BEGIN
    OPEN want FOR EXECUTE _query($2);
    res := results_eq($1, want, $3);
    CLOSE want;
    RETURN res;
END;

DECLARE
    have REFCURSOR;
    want REFCURSOR;
    res  TEXT;
BEGIN
    OPEN have FOR EXECUTE _query($1);
    OPEN want FOR SELECT $2[i]
    FROM generate_series(array_lower($2, 1), array_upper($2, 1)) s(i);
    res := results_eq(have, want, $3);
    CLOSE have;
    CLOSE want;
    RETURN res;
END;

DECLARE
    have REFCURSOR;
    res  TEXT;
BEGIN
    OPEN have FOR EXECUTE _query($1);
    res := results_eq(have, $2, $3);
    CLOSE have;
    RETURN res;
END;

DECLARE
    have REFCURSOR;
    want REFCURSOR;
    res  TEXT;
BEGIN
    OPEN have FOR EXECUTE _query($1);
    OPEN want FOR EXECUTE _query($2);
    res := results_eq(have, want, $3);
    CLOSE have;
    CLOSE want;
    RETURN res;
END;

DECLARE
    want REFCURSOR;
    res  TEXT;
BEGIN
    OPEN want FOR SELECT $2[i]
    FROM generate_series(array_lower($2, 1), array_upper($2, 1)) s(i);
    res := results_ne($1, want, $3);
    CLOSE want;
    RETURN res;
END;

DECLARE
    have       ALIAS FOR $1;
    want       ALIAS FOR $2;
    have_rec   RECORD;
    want_rec   RECORD;
    have_found BOOLEAN;
    want_found BOOLEAN;
    err_msg    text := 'details not available in pg <= 9.1';
BEGIN
    FETCH have INTO have_rec;
    have_found := FOUND;
    FETCH want INTO want_rec;
    want_found := FOUND;
    WHILE have_found OR want_found LOOP
        IF have_rec IS DISTINCT FROM want_rec OR have_found <> want_found THEN
            RETURN ok( true, $3 );
        ELSE
            FETCH have INTO have_rec;
            have_found := FOUND;
            FETCH want INTO want_rec;
            want_found := FOUND;
        END IF;
    END LOOP;
    RETURN ok( false, $3 );
EXCEPTION
    WHEN datatype_mismatch THEN
        GET STACKED DIAGNOSTICS err_msg = MESSAGE_TEXT;
        RETURN ok( false, $3 ) || E'\n' || diag(
            E'    Number of columns or their types differ between the queries' ||
            CASE WHEN have_rec::TEXT = want_rec::text THEN '' ELSE E':\n' ||
                '        have: ' || CASE WHEN have_found THEN have_rec::text ELSE 'NULL' END || E'\n' ||
                '        want: ' || CASE WHEN want_found THEN want_rec::text ELSE 'NULL' END
            END || E'\n        ERROR: ' || err_msg
        );
END;

DECLARE
    want REFCURSOR;
    res  TEXT;
BEGIN
    OPEN want FOR EXECUTE _query($2);
    res := results_ne($1, want, $3);
    CLOSE want;
    RETURN res;
END;

DECLARE
    have REFCURSOR;
    want REFCURSOR;
    res  TEXT;
BEGIN
    OPEN have FOR EXECUTE _query($1);
    OPEN want FOR SELECT $2[i]
    FROM generate_series(array_lower($2, 1), array_upper($2, 1)) s(i);
    res := results_ne(have, want, $3);
    CLOSE have;
    CLOSE want;
    RETURN res;
END;

DECLARE
    have REFCURSOR;
    res  TEXT;
BEGIN
    OPEN have FOR EXECUTE _query($1);
    res := results_ne(have, $2, $3);
    CLOSE have;
    RETURN res;
END;

DECLARE
    have REFCURSOR;
    want REFCURSOR;
    res  TEXT;
BEGIN
    OPEN have FOR EXECUTE _query($1);
    OPEN want FOR EXECUTE _query($2);
    res := results_ne(have, want, $3);
    CLOSE have;
    CLOSE want;
    RETURN res;
END;

DECLARE
    rec    RECORD;
BEGIN
    EXECUTE _query($1) INTO rec;
    IF NOT rec IS DISTINCT FROM $2 THEN RETURN ok(true, $3); END IF;
    RETURN ok(false, $3 ) || E'\n' || diag(
           '        have: ' || CASE WHEN rec IS NULL THEN 'NULL' ELSE rec::text END ||
        E'\n        want: ' || CASE WHEN $2  IS NULL THEN 'NULL' ELSE $2::text  END
    );
END;

DECLARE
    is_it boolean := _is_instead($1, $2, $3);
BEGIN
    IF is_it IS NOT NULL THEN RETURN ok( is_it, $4 ); END IF;
    RETURN ok( FALSE, $4 ) || E'\n' || diag(
        '    Rule ' || quote_ident($3) || ' does not exist'
    );
END;

DECLARE
    is_it boolean := _is_instead($1, $2);
BEGIN
    IF is_it IS NOT NULL THEN RETURN ok( is_it, $3 ); END IF;
    RETURN ok( FALSE, $3 ) || E'\n' || diag(
        '    Rule ' || quote_ident($2) || ' does not exist'
    );
END;

SELECT rule_is_on(
    $1, $2, $3, $4,
    'Rule ' || quote_ident($3) || ' should be on ' || _expand_on(_contract_on($4)::char)
    || ' to ' || quote_ident($1) || '.' || quote_ident($2)
);

DECLARE
    want char := _contract_on($4);
    have char := _rule_on($1, $2, $3);
BEGIN
    IF have IS NOT NULL THEN
        RETURN is( _expand_on(have), _expand_on(want), $5 );
    END IF;

    RETURN ok( false, $5 ) || E'\n' || diag(
        '    Rule ' || quote_ident($3) || ' does not exist on '
        || quote_ident($1) || '.' || quote_ident($2)
    );
END;

SELECT rule_is_on(
    $1, $2, $3,
    'Rule ' || quote_ident($2) || ' should be on '
    || _expand_on(_contract_on($3)::char) || ' to ' || quote_ident($1)
);

DECLARE
    want char := _contract_on($3);
    have char := _rule_on($1, $2);
BEGIN
    IF have IS NOT NULL THEN
        RETURN is( _expand_on(have), _expand_on(want), $4 );
    END IF;

    RETURN ok( false, $4 ) || E'\n' || diag(
        '    Rule ' || quote_ident($2) || ' does not exist on '
        || quote_ident($1)
    );
END;

SELECT schema_owner_is(
    $1, $2,
    'Schema ' || quote_ident($1) || ' should be owned by ' || quote_ident($2)
);

DECLARE
    owner NAME := _get_schema_owner($1);
BEGIN
    -- Make sure the schema exists.
    IF owner IS NULL THEN
        RETURN ok(FALSE, $3) || E'\n' || diag(
            E'    Schema ' || quote_ident($1) || ' does not exist'
        );
    END IF;

    RETURN is(owner, $2, $3);
END;

SELECT schema_privs_are(
    $1, $2, $3,
    'Role ' || quote_ident($2) || ' should be granted '
        || CASE WHEN $3[1] IS NULL THEN 'no privileges' ELSE array_to_string($3, ', ') END
        || ' on schema ' || quote_ident($1)
);

DECLARE
    grants TEXT[] := _get_schema_privs( $2, $1::TEXT );
BEGIN
    IF grants[1] = 'invalid_schema_name' THEN
        RETURN ok(FALSE, $4) || E'\n' || diag(
            '    Schema ' || quote_ident($1) || ' does not exist'
        );
    ELSIF grants[1] = 'undefined_role' THEN
        RETURN ok(FALSE, $4) || E'\n' || diag(
            '    Role ' || quote_ident($2) || ' does not exist'
        );
    END IF;
    RETURN _assets_are('privileges', grants, $3, $4);
END;

